import React from "react";
import "./navbar.css";

function navbar({ setHomeStyle, setCartStyle }) {
  // displays navigation bar

  function viewCart(e) {
    e.preventDefault();
    setCartStyle({ display: "none" });
    setHomeStyle({ display: "block" });
  }
  function viewHome(e) {
    e.preventDefault();
    setCartStyle({ display: "block" });
    setHomeStyle({ display: "none" });
  }
  return (
    <nav className="navbar">
      <ul>
        <li>
          <a onClick={(e) => viewHome(e)}>Home</a>
        </li>
        <li>
          <a onClick={(e) => viewCart(e)}>shopping Cart</a>
        </li>
      </ul>
    </nav>
  );
}

export default navbar;
