import React from "react";

function Navbar({ setHomeStyle, setCartStyle }) {
  // displays navigation bar

  const navStyle = {
    backgroundColor: "#f2f2f2",
    padding: "10px",
    marginBottom: "20px",
    display: "flex",
    justifyContent: "center",
  };

  const linkStyle = {
    marginRight: "10px",
    color: "#333",
    textDecoration: "none",
    fontSize: "16px",
    fontWeight: "bold",
  };

  function viewCart(e) {
    e.preventDefault();
    setCartStyle({ display: "none" });
    setHomeStyle({ display: "block" });
  }

  function viewHome(e) {
    e.preventDefault();
    setCartStyle({ display: "block" });
    setHomeStyle({ display: "none" });
  }

  return (
    <nav style={navStyle}>
      <ul>
        <li>
          <a style={linkStyle} onClick={(e) => viewHome(e)} href="/">
            Home
          </a>
        </li>
        <li>
          <a style={linkStyle} onClick={(e) => viewCart(e)} href="/">
            Shopping Cart
          </a>
        </li>
      </ul>
    </nav>
  );
}

export default Navbar;
