import React, { useEffect, useState } from "react";

function Cart({ addedSpinners }) {
  const [count, setCount] = useState(1);

  function handleCount(e) {
    if (e.target.textContent === "+") {
      setCount((count) => count + 1);
    } else {
      if (count > 1) {
        setCount((count) => count - 1);
      }
    }
  }

  const [subtotal, setSubtotal] = useState(0);

  useEffect(() => {
    let newSubtotal = 0;
    addedSpinners.forEach((spinner) => {
      newSubtotal += spinner.price * count;
    });
    setSubtotal(newSubtotal);
  }, [addedSpinners, count]);

  function handleCheckout() {
    alert("Thank you for your purchase");
  }

  return (
    <div id="cart-div">
      <div id="cart-list">
        <h3 id="cartHeader">Shopping Cart</h3>
        {addedSpinners.map((spinner) => {
          return (
            <div className="cart-item" key={spinner.id}>
              <section className="cart-image">
                <img src={spinner.image} alt="cart item" />
              </section>
              <p className="cart-details">
                {spinner.name} <br />
                {spinner.price} <br />
              </p>
              <button onClick={handleCount}>-</button>
              {count} <button onClick={handleCount}>+</button>
            </div>
          );
        })}
      </div>
      <section id="subtotals-div">
        <h3> Subtotal: {subtotal}</h3>
      </section>
      <section id="checkout">
        <button onClick={handleCheckout}>Checkout</button>
      </section>
    </div>
  );
}

export default Cart;
