import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faShoppingCart } from "@fortawesome/free-solid-svg-icons";

function Navbar({ setHomeStyle, setCartStyle }) {
  // displays navigation bar

  const navStyle = {
    backgroundColor: "#f2f2f2",
    padding: "10px",
    marginBottom: "20px",
    display: "flex",
    justifyContent: "center",
  };

  const linkStyle = {
    marginRight: "10px",
    color: "#333",
    textDecoration: "none",
    fontSize: "16px",
    fontWeight: "bold",
    listStyle: "none",
    display: "inline",
  };

  function viewCart(e) {
    e.preventDefault();
    setCartStyle({ display: "none" });
    setHomeStyle({ display: "block" });
  }

  function viewHome(e) {
    e.preventDefault();
    setCartStyle({ display: "block" });
    setHomeStyle({ display: "none" });
  }

  return (
    <nav style={navStyle}>
      <ul style={{ padding: "0" }}>
        <li style={{ display: "inline" }}>
          <a style={linkStyle} onClick={(e) => viewHome(e)} href="/">
            Home
          </a>
        </li>
        <li style={{ display: "inline" }}>
          <a style={linkStyle} onClick={(e) => viewCart(e)} href="/">
            <FontAwesomeIcon icon={faShoppingCart} /> Shopping Cart
          </a>
        </li>
      </ul>
    </nav>
  );
}

export default Navbar;
